	private static Server newServer(Class<?> application_class, String api_servlet_class_name, String public_resources_context, String public_resources_dir) throws Exception {
		String runtime_resources_path = System.getProperty("java.io.tmpdir") + System.getProperty("file.separator") + UUID.randomUUID().toString();
