		handler.setInitParameter("org.eclipse.rap.applicationConfiguration", application_class.getCanonicalName());
		handler.addEventListener(new RWTServletContextListener());
		handler.addServlet(RWTServlet.class.getName(), "/");
        handler.addServlet(DefaultServlet.class, "/rwt-resources/*");
        if(public_resources_dir != null) {
